import React, { useEffect, useState } from 'react';
import axios from 'axios';
import UpdateForm from './Update';

function StudentList() {
	const [students, setStudents] = useState([]);
	const [ifUpdate, showUpdate] = useState(false);
	const [newName, setNewName] = useState('');
	const [newPhone, setNewPhone] = useState(0);
	const [newEmail, setNewEmail] = useState('');
	const [newClass, setNewClass] = useState('');
	const [searchName, setSearchName] = useState('');
	useEffect(() => {
		axios
			.get('http://localhost:3001/read')
			.then((res) => setStudents(res.data))
			.catch((err) => console.log(err));
	}, []);
	const updateStudent = (id) => {
		axios.put('http://localhost:3001/update', {
			id: id,
			newName: newName,
			newPhone: newPhone,
			newEmail: newEmail,
			newClass: newClass,
		});
	};
	const deleteStudent = (id) => {
		axios.delete(`http://localhost:3001/delete/${id}`);
	};
	const searchStudent = (name) => {
		axios.post(`http://localhost:3001/student/${name}`, {
			name: searchName,
		});
	};
	console.log(setStudents);
	return (
		<div>
			<h1>Students</h1>

			{students.map((student, index) => {
				return (
					<div
						key={index}
						style={{
							margin: '26px',
							fontSize: '17px',
							border: '1px solid black',
							padding: '10px',
						}}
					>
						<input
							type="text"
							placeholder="Search Name.."
							onChange={(e) => setSearchName(e.target.value)}
						/>
						<button
							className="search"
							onClick={() => searchStudent(student.studentName)}
						>
							<i className="fa fa-search fa2x"></i>
						</button>
						<p>Name: {student.studentName}</p>
						<p>Email: {student.email}</p>
						<p>Phone: +254{student.phoneNumber}</p>
						<p>Class: {student.classGroup}</p>
						<div className="btnDiv">
							{ifUpdate === true ? (
								<div>
									<UpdateForm
										newName={newName}
										setNewName={setNewName}
										newPhone={newPhone}
										setNewPhone={setNewPhone}
										newEmail={newEmail}
										setNewEmail={setNewEmail}
										newClass={newClass}
										setNewClass={setNewClass}
									/>
									<button onClick={() => updateStudent(student._id)}>
										Update
									</button>
								</div>
							) : (
								<div>
									<button onClick={() => showUpdate(true)}>Update</button>
								</div>
							)}
							<button onClick={() => deleteStudent(student._id)}>Delete</button>
						</div>
					</div>
				);
			})}
		</div>
	);
}

export default StudentList;
