import React from 'react';
import axios from 'axios';
import './App.css';

function App() {
	const [name, setName] = React.useState('');
	const [phone, setPhone] = React.useState(0);
	const [email, setEmail] = React.useState('');
	const [classSet, setClassSet] = React.useState('');
	const [birthDay, setBirthDay] = React.useState('');
	const addStudents = () => {
		axios.post('http://localhost:3001/addstudent', {
			studentName: name,
			phoneNumber: phone,
			email: email,
			classGroup: classSet,
			dateOfBirth: birthDay,
		});
		console.log(name, phone, email, classSet, birthDay);
	};
	return (
		<div className="App">
			<h1> MERN CRUD</h1>
			<label>Student Name</label>
			<input
				type="text"
				placeholder="Name.."
				onChange={(event) => setName(event.target.value)}
			/>
			<label>Phone No</label>
			<input
				type="number"
				placeholder="Phone.."
				onChange={(event) => setPhone(event.target.value)}
			/>
			<label>Email</label>
			<input
				type="text"
				placeholder="Email.."
				onChange={(event) => setEmail(event.target.value)}
			/>
			<label>Class</label>
			<input
				type="text"
				placeholder="Class.."
				onChange={(event) => setClassSet(event.target.value)}
			/>
			<label>Date of Birth</label>
			<input
				type="text"
				placeholder="D.O.B..."
				onChange={(event) => setBirthDay(event.target.value)}
			/>
			<button onClick={() => addStudents()}>Add</button>
		</div>
	);
}

export default App;
